import React, {Component} from 'react';

class Answer extends Component {

    displayButton = () => {
        const next = document.getElementById("next");
        console.log(next)
        next.style.display = "block";
    };


    displayElement = () => {
        var no = new Audio('/music/no.mp3');
        var yes = new Audio('/music/yes.mp3');
        switch (this.props.answer.ans) {
            case 1:
                return (
                    <>
                        <a onClick={() => {
                            yes.play();
                            this.displayButton;
                        }}>
                            <img src={this.props.answer.img1} className="hinh81" width="100px" height="100px "
                                 style={{marginRight: 50, marginBottom: 50, marginTop: 100}}/>
                        </a>

                        <a onClick={() => no.play()}>
                            <img src={this.props.answer.img2} className="hinh82" width="100px" height="100px "
                                 style={{marginRight: 50, marginBottom: 50}}/>
                        </a>

                        <a onClick={() => no.play()}>
                            <img src={this.props.answer.img3} className="hinh84" width="100px" height="100px "
                                 style={{marginRight: 50, marginBottom: 50}}/>
                        </a>

                        <a onClick={() => no.play()}>
                            <img src={this.props.answer.img4} className="hinh83" width="100px" height="100px "
                                 style={{marginRight: 50, marginBottom: 50}}/>
                        </a>
                    </>
                )
            case 2:
                return (
                    <>
                        <a onClick={() => no.play()}>
                            <img src={this.props.answer.img1} className="hinh81" width="100px" height="100px "
                                 style={{marginRight: 50, marginBottom: 50, marginTop: 100}}/>
                        </a>

                        <a onClick={() => {
                            yes.play();
                            this.displayButton();
                        }}>
                            <img src={this.props.answer.img2} className="hinh82" width="100px" height="100px "
                                 style={{marginRight: 50, marginBottom: 50}}/>
                        </a>

                        <a onClick={() => no.play()}>
                            <img src={this.props.answer.img3} className="hinh84" width="100px" height="100px "
                                 style={{marginRight: 50, marginBottom: 50}}/>
                        </a>

                        <a onClick={() => no.play()}>
                            <img src={this.props.answer.img4} className="hinh83" width="100px" height="100px "
                                 style={{marginRight: 50, marginBottom: 50}}/>
                        </a>
                    </>
                )
            case 3:
                return (
                    <>
                        <a onClick={() => no.play()}>
                            <img src={this.props.answer.img1} className="hinh81" width="100px" height="100px "
                                 style={{marginRight: 50, marginBottom: 50, marginTop: 100}}/>
                        </a>

                        <a onClick={() => no.play()}>
                            <img src={this.props.answer.img2} className="hinh82" width="100px" height="100px "
                                 style={{marginRight: 50, marginBottom: 50}}/>
                        </a>

                        <a onClick={() => {
                            yes.play();
                            this.displayButton();
                        }}>
                            <img src={this.props.answer.img3} className="hinh84" width="100px" height="100px "
                                 style={{marginRight: 50, marginBottom: 50}}/>
                        </a>

                        <a onClick={() => no.play()}>
                            <img src={this.props.answer.img4} className="hinh83" width="100px" height="100px "
                                 style={{marginRight: 50, marginBottom: 50}}/>
                        </a>
                    </>
                )
            default:
                return (
                    <>
                        <a onClick={() => no.play()}>
                            <img src={this.props.answer.img1} className="hinh81" width="100px" height="100px "
                                 style={{marginRight: 50, marginBottom: 50, marginTop: 100}}/>
                        </a>

                        <a onClick={() => no.play()}>
                            <img src={this.props.answer.img2} className="hinh82" width="100px" height="100px "
                                 style={{marginRight: 50, marginBottom: 50}}/>
                        </a>

                        <a onClick={() => no.play()}>
                            <img src={this.props.answer.img3} className="hinh84" width="100px" height="100px "
                                 style={{marginRight: 50, marginBottom: 50}}/>
                        </a>

                        <a onClick={() => {
                            yes.play();
                            this.displayButton();
                        }}>
                            <img src={this.props.answer.img4} className="hinh83" width="100px" height="100px "
                                 style={{marginRight: 50, marginBottom: 50}}/>
                        </a>
                    </>
                )
        }
    }


    render() {

        return (
            <>
                <div className="img9" style={{textAlign: 'center'}}>
                    {this.displayElement()}
                </div>
                <button id="next" style={{
                    backgroundColor: 'rgb(47, 248, 255)',
                    display: 'none',
                    borderRadius: 10,
                    width: 100,
                    height: 50,
                    marginLeft: 800
                }}>NEXT
                </button>
            </>
        );
    }
}

export default Answer;
