import React, {Component} from 'react';
import "./2.css"
import Question from "./Question";
import Answer from "./Answer";

class HomeNumber extends Component {
    constructor(props) {
        super(props);
        this.state = {
            numbers: ["one", "two", "three", "four", "five", "six", "seven", "eight", "nine"],
            number: "",
            answers: [
                {
                    "img1": "/Images/1.png",
                    "img2": "/Images/3.png",
                    "img3": "/Images/8.png",
                    "img4": "/Images/4.png",
                    code: "one"
                },
                {
                    "img1": "/Images/1.png",
                    "img2": "/Images/2.png",
                    "img3": "/Images/6.png",
                    "img4": "/Images/9.png",
                    code: "six"
                },
                {
                    "img1": "/Images/7.png",
                    "img2": "/Images/2.png",
                    "img3": "/Images/5.png",
                    "img4": "/Images/9.png",
                    code: "five"
                },
                {
                    "img1": "/Images/3.png",
                    "img2": "/Images/4.png",
                    "img3": "/Images/9.png",
                    "img4": "/Images/2.png",
                    code: "two"
                },
                {
                    "img1": "/Images/8.png",
                    "img2": "/Images/3.png",
                    "img3": "/Images/5.png",
                    "img4": "/Images/9.png",
                    code: "three"
                },
                {
                    "img1": "/Images/2.png",
                    "img2": "/Images/9.png",
                    "img3": "/Images/5.png",
                    "img4": "/Images/7.png",
                    code: "nine"
                },
                {
                    "img1": "/Images/2.png",
                    "img2": "/Images/6.png",
                    "img3": "/Images/4.png",
                    "img4": "/Images/9.png",
                    code: "four"
                },
                {
                    "img1": "/Images/4.png",
                    "img2": "/Images/3.png",
                    "img3": "/Images/9.png",
                    "img4": "/Images/7.png",
                    code: "four"
                },
            ]
        }
    }

    toSpeak = (id) => {
        var msg = new SpeechSynthesisUtterance(id);
        window.speechSynthesis.speak(msg);
    }

    componentDidMount() {
        const presentNumber = this.state.numbers.find(number => number === this.props.match.params.number);
        console.log(presentNumber);
        if (presentNumber) {
            this.setState({
                number: presentNumber
            })
            // this.toSpeak(presentNumber)
        } else {
            this.props.history.push("/home-number/one")
        }
    }

    again = () => {
        this.props.history.push("/home-number/one");
        window.location.reload();
    }
    exit = () => {
        this.props.history.push("/chude2");
        window.location.reload();
    }

    render() {
        return (
            <div id="numberplay">
                <div>
                    <h1 className="game-title">Play-Game</h1>
                </div>
                <div>
                    <h1 className="win" style={{display: 'none', marginTop: '50px'}}>YOU ARE WINER</h1>
                </div>
                <a id="again" onClick={() => this.again()}
                   style={{display: 'none', marginTop: 50, cursor: 'pointer'}}>Again</a>
                <a id="exit" onClick={() => this.exit()}
                   style={{display: 'none', marginTop: 50, cursor: 'pointer'}}>Exit</a>
                <Question
                    code={this.state.number}
                />
                <Answer/>
            </div>
        );
    }
}

export default HomeNumber;
